(553, 1, '2019-03-07 12:37:16', '2019-03-07 12:37:16', '[vc_row][vc_column][vc_row_inner][vc_column_inner][vc_column_text][metaslider id=\"540\"][/vc_column_text][vc_column_text]\n<div class=\"section-01 row\" style=\"background: #eee; margin-top: -20px; margin-bottom: 20px;\">\n<div class=\"col-md-3\" style=\"border-right: 2px solid #37a000;\">\n<h2 style=\"text-align: left; font-size: 20px; font-weight: bolder; color: #000;\">Hire for any scope\nof work:</h2>\n</div>\n<div class=\"col-md-3\" style=\"border-right: 2px solid #37a000;\">\n<h2 style=\"text-align: left; font-size: 15px; font-weight: bolder; color: #000;\">Short-trem tasks</h2>\n<p style=\"text-align: left; font-size: 15px;\">Build a pool of diverse experts for tasks</p>\n\n</div>\n<div class=\"col-md-3\" style=\"border-right: 2px solid #37a000;\">\n<h2 style=\"text-align: left; font-size: 15px; font-weight: bolder; color: #000;\">Recurring Projects</h2>\n<p style=\"text-align: left; font-size: 15px;\">Have a go-to team with specialized skills</p>\n\n</div>\n<div class=\"col-md-3\">\n<h2 style=\"text-align: left; font-size: 15px; font-weight: bolder; color: #000;\">Full Time Contract Work</h2>\n<p style=\"text-align: left; font-size: 15px;\">Expand your staff with a dedicated team</p>\n\n</div>\n</div>\n[/vc_column_text][vc_column_text]\n<div class=\"row\" style=\"background: #fff; padding: 30px 0;\"><center style=\"font-weight: bold; font-size: 40px; color: #000;\">Build a pool of trusted experts for your team</center></div>\n<div class=\"row\" style=\"background: #fff;\">\n<div class=\"col-md-3\">\n<div class=\"center-block\" style=\"width: 75%; border: 4px solid #eee; border-radius: 5px; box-sizing: border-box;\"><center style=\"color: #000; padding: 30px 0;\">\n<img src=\"http://localhost/Home_Project/FreelanceEngine/wp-content/uploads/2019/03/web-developers.ffc6ae343cb6.svg\" alt=\"\" />\nWeb Developers</center></div>\n</div>\n<div class=\"col-md-3\">\n<div class=\"center-block\" style=\"width: 75%; border: 4px solid #eee; border-radius: 5px; box-sizing: border-box;\"><center style=\"color: #000; padding: 30px 0;\">\n<img src=\"http://localhost/Home_Project/FreelanceEngine/wp-content/uploads/2019/03/mobile-developers.8ddabde13572.svg\" alt=\"\" />\nMobile Developers</center></div>\n</div>\n<div class=\"col-md-3\">\n<div class=\"center-block\" style=\"width: 75%; border: 4px solid #eee; border-radius: 5px; box-sizing: border-box;\"><center style=\"color: #000; padding: 30px 0;\">\n<img src=\"http://localhost/Home_Project/FreelanceEngine/wp-content/uploads/2019/03/designers.c3e98b7c60ed.svg\" alt=\"\" />\nCreative Designers</center></div>\n</div>\n<div class=\"col-md-3\">\n<div class=\"center-block\" style=\"width: 75%; border: 4px solid #eee; border-radius: 5px; box-sizing: border-box;\"><center style=\"color: #000; padding: 30px 0;\">\n<img src=\"http://localhost/Home_Project/FreelanceEngine/wp-content/uploads/2019/03/writers.e355e2760b23.svg\" alt=\"\" />\nContent Writers</center></div>\n</div>\n&nbsp;\n<div class=\"col-md-3\">\n<div class=\"center-block\" style=\"width: 75%; border: 4px solid #eee; border-radius: 5px; box-sizing: border-box;\"><center style=\"color: #000; padding: 30px 0;\">\n<img src=\"http://localhost/Home_Project/FreelanceEngine/wp-content/uploads/2019/03/customer-service.11fc77c2bc5d.svg\" alt=\"\" />\nCustomer Service</center></div>\n</div>\n<div class=\"col-md-3\">\n<div class=\"center-block\" style=\"width: 75%; border: 4px solid #eee; border-radius: 5px; box-sizing: border-box;\"><center style=\"color: #000; padding: 30px 0;\">\n<img src=\"http://localhost/Home_Project/FreelanceEngine/wp-content/uploads/2019/03/sales-marketing.ad2866f02172.svg\" alt=\"\" />\nSales Marketing</center></div>\n</div>\n<div class=\"col-md-3\">\n<div class=\"center-block\" style=\"width: 75%; border: 4px solid #eee; border-radius: 5px; box-sizing: border-box;\"><center style=\"color: #000; padding: 30px 0;\">\n<img src=\"http://localhost/Home_Project/FreelanceEngine/wp-content/uploads/2019/03/virtual-assistants.8ceafe63a521.svg\" alt=\"\" />\nVirtual Supports</center></div>\n</div>\n<div class=\"col-md-3\">\n<div class=\"center-block\" style=\"width: 75%; border: 4px solid #eee; border-radius: 5px; box-sizing: border-box;\"><center style=\"color: #000; padding: 30px 0;\">\n<img src=\"http://localhost/Home_Project/FreelanceEngine/wp-content/uploads/2019/03/accounting-consulting.950b1d199e7c.svg\" alt=\"\" />\nAccounting</center></div>\n</div>\n</div>\n<div class=\"row\"><center>\n<h3 style=\"color: #000; padding: 30px 0; font-weight: bold; font-size: 40px;\">How its work</h3>\n&nbsp;\n\n</center>\n<div class=\"col-md-3\">\n<div class=\"center-block\"><img class=\"center-block\" style=\"width: 101px;\" src=\"http://localhost/Home_Project/FreelanceEngine/wp-content/uploads/2019/03/interview.png\" alt=\"\" />\n<h3 style=\"text-align: center; color: #000; font-size: 20px;\">Post a job (itâs free)</h3>\n<p style=\"text-align: center;\">Tell us about your project. Upwork connects you with top talent around the world, or near you.</p>\n\n</div>\n</div>\n<div class=\"col-md-3\">\n<div class=\"center-block\"><img class=\"center-block\" style=\"width: 101px;\" src=\"http://localhost/Home_Project/FreelanceEngine/wp-content/uploads/2019/03/document.png\" alt=\"\" />\n<h3 style=\"text-align: center; color: #000; font-size: 20px;\">Freelancers come to you</h3>\n<p style=\"text-align: center;\">Get qualified proposals within 24 hours. Compare bids, reviews, and prior work. Interview favorites and hire the best fit.</p>\n\n</div>\n</div>\n<div class=\"col-md-3\">\n<div class=\"center-block\"><img class=\"center-block\" style=\"width: 101px;\" src=\"http://localhost/Home_Project/FreelanceEngine/wp-content/uploads/2019/03/chatting.png\" alt=\"\" />\n<h3 style=\"text-align: center; color: #000; font-size: 20px;\">Collaborate easily</h3>\n<p style=\"text-align: center;\">Use Upwork to chat or video call, share files, and track project milestones from your desktop or mobile.</p>\n\n</div>\n</div>\n<div class=\"col-md-3\">\n<div class=\"center-block\"><img class=\"center-block\" style=\"width: 101px;\" src=\"http://localhost/Home_Project/FreelanceEngine/wp-content/uploads/2019/03/payment.png\" alt=\"\" />\n<h3 style=\"text-align: center; color: #000; font-size: 20px;\">Payment simplified</h3>\n<p style=\"text-align: center;\">Pay hourly or fixed-price and receive invoices through Upwork. Only pay for work you authorize</p>\n\n</div>\n</div>\n</div>\n[/vc_column_text][/vc_column_inner][/vc_row_inner][vc_tabs][vc_tab title=\"Latest Projects\" tab_id=\"1416188742-1-90\"][fre_block_project showposts=\"8\" query=\"recent\" paginate=\"page\"][/vc_tab][vc_tab title=\"Featured projects\" tab_id=\"1416188742-2-84\"][fre_block_project showposts=\"8\" paginate=\"page\"][/vc_tab][/vc_tabs][vc_tabs][vc_tab title=\"Latest Profiles\" tab_id=\"1416188742-1-90\"][fre_block_profile showposts=\"8\" paginate=\"page\"][/vc_tab][vc_tab title=\"Top Rating Profiles\" tab_id=\"1416188742-2-84\"][fre_block_profile showposts=\"8\" paginate=\"page\"][/vc_tab][/vc_tabs][fre_status_block s_description=\"We love to build awesome solutions for your business.\nWith all our passion and experience in WP.\"][/vc_column][/vc_row][vc_row][vc_column][fre_testimonial_block][/vc_column][/vc_row][vc_row][vc_column][fre_pricing_block number_plan=\"3\"][/vc_column][/vc_row]', 'Home 3', '', 'inherit', 'closed', 'closed', '', '348-autosave-v1', '', '', '2019-03-07 12:37:16', '2019-03-07 12:37:16', '', 348, 'http://localhost/Home_Project/FreelanceEngine/2019/03/07/348-autosave-v1/', 0, 'revision', '', 0),
